from pybricks.parameters import Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase

# Set up all devices.
left = Motor(Port.A, Direction.COUNTERCLOCKWISE)
right = Motor(Port.B, Direction.CLOCKWISE)
robot = DriveBase(left, right, 56, 128)


# The main program starts here.
# The following three commands don't stop.
robot.straight(250, then=Stop.NONE)
robot.arc(100, angle=180, then=Stop.NONE)
robot.straight(250, then=Stop.NONE)
# We still want to stop normally at the end.
robot.arc(100, angle=180)
