from pybricks.parameters import Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase
from pybricks.tools import wait

# Set up all devices.
left = Motor(Port.A, Direction.COUNTERCLOCKWISE)
right = Motor(Port.B, Direction.CLOCKWISE)
robot = DriveBase(left, right, 56, 128)


# The main program starts here.
robot.straight(250)
# Pick up the robot and try to turn the wheels.
# They will try to stay in place.
wait(5000)
robot.straight(250, Stop.BRAKE)
# Pick up the robot and try to turn the wheels.
# You can, with some resistance.
wait(5000)
# Pick up the robot and try to turn the wheels.
# You can, quite easily.
robot.straight(250, Stop.COAST)
wait(5000)
