from pybricks.parameters import Color, Port
from pybricks.pupdevices import ColorLightMatrix
from pybricks.tools import wait

# Set up all devices.
matrix = ColorLightMatrix(Port.A)


# The main program starts here.
matrix.on(Color.ORANGE)
wait(1000)
matrix.on([Color.RED, Color.ORANGE, Color.YELLOW, Color.GREEN, Color.NONE, Color.CYAN, Color.BLUE, Color.MAGENTA, Color.WHITE])
wait(2000)
matrix.off()
wait(1000)
