from pybricks.parameters import Port
from pybricks.pupdevices import UltrasonicSensor
from pybricks.tools import wait

# Set up all devices.
eyes = UltrasonicSensor(Port.D)


# The main program starts here.
# Turn the sensor lights on.
eyes.lights.on(100)
wait(1000)
# Turn the sensor lights off.
eyes.lights.on(0)
wait(1000)
# You can control each light separately with a list of values.
eyes.lights.on([100, 0, 100, 0])
wait(1000)
