from pybricks.hubs import PrimeHub
from pybricks.parameters import Button, Direction, Port
from pybricks.pupdevices import Motor, UltrasonicSensor
from pybricks.robotics import DriveBase
from pybricks.tools import wait

# Set up all devices.
hub = PrimeHub()
sensor = UltrasonicSensor(Port.D)
left = Motor(Port.A, Direction.COUNTERCLOCKWISE)
right = Motor(Port.B, Direction.CLOCKWISE)
robot = DriveBase(left, right, 56, 128)


# The main program starts here.
robot.drive(200, 0)
while not (sensor.distance() <= 500 or any(hub.buttons.pressed())):
    wait(0)
print('Distance:', sensor.distance())
print('Left bumper pressed?', Button.RIGHT in hub.buttons.pressed())
print('Right bumper pressed?', Button.LEFT in hub.buttons.pressed())
robot.stop()
