from pybricks.parameters import Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.tools import wait

from allow_missing_motors import Motor

# Set up all devices.
A = Motor(Port.A, Direction.CLOCKWISE)
B = Motor(Port.B, Direction.CLOCKWISE)


# The main program starts here.
# This will move motor A. Or skip it if nothing is plugged in.
A.dc(50)
wait(1000)
A.dc(-75)
wait(1000)
# This will turn the motor by 360 degrees.
# Or skip it if nothing is plugged in.
# Or skip it if the motor does not have rotation sensors.
A.run_angle(500, 360)
