from pybricks.parameters import Color
from pybricks.tools import wait

from duplo import DuploTrain, play_sound, set_light, start_driving

# Initialize variables.
train = DuploTrain()


# The main program starts here.
# This is how to control the light and sound.
set_light(train, Color.RED)
wait(1000)
play_sound(train, 'depart')
wait(1000)
# This is how to drive, stop, and reverse.
start_driving(train, 50)
wait(1000)
start_driving(train, 0)
wait(1000)
start_driving(train, -50)
wait(1000)
