from pybricks.parameters import Color, Direction, Port
from pybricks.pupdevices import ColorDistanceSensor, DCMotor

# Set up all devices.
sensor = ColorDistanceSensor(Port.A)
motor = DCMotor(Port.B, Direction.CLOCKWISE)


# The main program starts here.
while True:
    # Start moving on seeing green.
    if sensor.color() == Color.GREEN:
        motor.dc(100)
    # Stop moving on seeing red.
    if sensor.color() == Color.RED:
        motor.stop()
