from pybricks.hubs import TechnicHub
from pybricks.parameters import Axis, Button, Color, Direction, Port, Stop
from pybricks.pupdevices import Motor, Remote
from pybricks.tools import wait

# Set up all devices.
hub = TechnicHub(top_side=Axis.Z, front_side=Axis.X, broadcast_channel=5)
left = Motor(Port.C, Direction.CLOCKWISE)
right = Motor(Port.D, Direction.COUNTERCLOCKWISE)
turntable = Motor(Port.B, Direction.CLOCKWISE)
remote = Remote(timeout=None)

# Initialize variables.
boom_speed = 0
jib_speed = 0
hoist_speed = 0

def drive_using_the_buttons():
    # Use the left/right red buttons for the turntable motor.
    if Button.LEFT in remote.buttons.pressed():
        turntable.run(1000)
    elif Button.RIGHT in remote.buttons.pressed():
        turntable.run(-1000)
    else:
        turntable.stop()
    # Use the left +/- for the left track.
    if Button.LEFT_PLUS in remote.buttons.pressed():
        left.run(1000)
    elif Button.LEFT_MINUS in remote.buttons.pressed():
        left.run(-1000)
    else:
        left.stop()
    # Use the right +/- for the left track.
    if Button.RIGHT_PLUS in remote.buttons.pressed():
        right.run(1000)
    elif Button.RIGHT_MINUS in remote.buttons.pressed():
        right.run(-1000)
    else:
        right.stop()


# The main program starts here.
left.control.limits(acceleration=2500)
right.control.limits(acceleration=2500)
while True:
    # First, we are in drive mode, where we operate the base
    # using the buttons, until you press the center button to
    # change the mode.
    remote.light.on(Color.RED)
    while Button.CENTER in remote.buttons.pressed():
        wait(1)
    while not Button.CENTER in remote.buttons.pressed():
        drive_using_the_buttons()
        wait(50)
    # Now we are in crane operation mode. Here we stop driving and
    # broadcast the button values to the other hub, so it can operate
    # the crane functions based on your input. Until you press the
    # center button again.
    remote.light.on(Color.GREEN)
    right.stop()
    left.stop()
    turntable.stop()
    while Button.CENTER in remote.buttons.pressed():
        wait(1)
    while not Button.CENTER in remote.buttons.pressed():
        boom_speed = 100 if Button.LEFT_PLUS in remote.buttons.pressed() else (-100 if Button.LEFT_MINUS in remote.buttons.pressed() else 0)
        jib_speed = 100 if Button.RIGHT_PLUS in remote.buttons.pressed() else (-100 if Button.RIGHT_MINUS in remote.buttons.pressed() else 0)
        hoist_speed = 100 if Button.RIGHT in remote.buttons.pressed() else (-100 if Button.LEFT in remote.buttons.pressed() else 0)
        hub.ble.broadcast([boom_speed, jib_speed, hoist_speed])
        wait(50)
